contracts/AllowedColorsStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AllowedColorsStorage
 * @dev Color indexes need to be restricted per tribe prior to NFT deploy
 */
contract AllowedColorsStorage is Ownable {
    /// @dev Mapping if color is allowed for selected tribe
    mapping(uint256 => mapping(uint256 => bool)) private _tribeColorAllowed;

    constructor() {}

    /**
     * @notice Set allowed on a given a list of colors
     * @param tribe Tribe ID 1-10
     * @param colors List of colors to set for tribe
     * @param allowed Bool if the color list should be made allowed or not
     */
    function setAllowedColorsOnTribe(uint256 tribe, uint256[] memory colors, bool allowed) external onlyOwner {
        require(tribe > 0 && tribe < 10, "Invalid tribe provided");
        for (uint256 i = 0; i < colors.length; i++) {
            _toggleColorAllowed(tribe, colors[i], allowed);
        }
    }

    /**
     * @notice Toggle color allowed on and off for a tribe
     * @param tribe Tribe ID
     * @param color Trait ID
     * @param allowed Bool if the color should be made allowed or not
     * @dev Defaults to false if never set
     */
    function _toggleColorAllowed(uint256 tribe, uint256 color, bool allowed) private {
        _tribeColorAllowed[tribe][color] = allowed;
    }

    /**
     * @notice Check if color is allowed for a tribe
     * @param tribe Tribe ID
     * @param color Trait ID
     * @return True if color is allowed for tribe
     */
    function isAllowedColor(uint256 tribe, uint256 color) public view returns (bool) {
        return _tribeColorAllowed[tribe][color];
    }
}

contracts/BalanceManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

contract BalanceManager is Initializable, OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using ECDSAUpgradeable for bytes32;

    event NFTLDeposited(address indexed by, uint256 amount);
    event MaintainerUpdated(address indexed by, address indexed oldMaintainer, address indexed newMaintainer);
    event NFTLWithdrawn(address indexed by, address indexed beneficiary, uint256 amount);
    event NFTLWithdrawnByDAO(address indexed by, address indexed beneficiary, uint256 amount);

    /// @dev NFTL token address
    address public nftl;

    /// @dev User -> Total deposit amount
    mapping(address => uint256) private userDeposits;

    /// @dev User -> Total withdrawal amount
    mapping(address => uint256) private userWithdrawals;

    /// @dev User -> Nonce
    mapping(address => uint256) public nonce;

    /// @dev Signature -> Bool
    mapping(bytes => bool) public signatures;

    /// @dev Maintainer address
    address public maintainer;

    function initialize(address _nftl, address _maintainer) public initializer {
        __Ownable_init();

        nftl = _nftl;
        maintainer = _maintainer;
    }

    /**
     * @notice Deposit NFTL tokens into the contract
     * @param _amount Deposit amount
     */
    function deposit(uint256 _amount) external {
        IERC20Upgradeable(nftl).safeTransferFrom(msg.sender, address(this), _amount);
        userDeposits[msg.sender] += _amount;

        emit NFTLDeposited(msg.sender, _amount);
    }

    /**
     * @notice Withdraw NFTL tokens from the contract to the user
     * @param _amount NFTL token amount to withdraw
     * @param _nonce Nonce
     * @param _expireAt Expiration time
     * @param _signature Signature
     */
    function withdraw(uint256 _amount, uint256 _nonce, uint256 _expireAt, bytes memory _signature) external {
        // check if the nonce is matched
        require(nonce[msg.sender] == _nonce, "mismatched nonce");
        nonce[msg.sender] += 1;

        // check if the request is not expired
        require(block.timestamp <= _expireAt, "expired withdrawal request");

        // check if the signature was already used
        require(!signatures[_signature], "used signature");
        signatures[_signature] = true;

        // check the signer
        bytes32 data = keccak256(abi.encodePacked(msg.sender, _amount, _nonce, _expireAt));
        require(data.toEthSignedMessageHash().recover(_signature) == maintainer, "wrong signer");

        // update the withdrawal amount
        userWithdrawals[msg.sender] += _amount;

        // transfer tokens to the user
        IERC20Upgradeable(nftl).safeTransfer(msg.sender, _amount);

        emit NFTLWithdrawn(msg.sender, msg.sender, _amount);
    }

    /**
     * @notice Update maintianer address
     * @dev Only owner
     * @param _maintainer New maintainer address
     */
    function updateMaintainer(address _maintainer) external onlyOwner {
        emit MaintainerUpdated(msg.sender, maintainer, _maintainer);

        maintainer = _maintainer;
    }

    /**
     * @notice Allow DAO to withdraw NFTL token from the contract
     * @dev Only owner
     * @param _beneficiary Beneficiary address
     * @param _amount NFTL token amount to withdraw
     */
    function withdrawByDAO(address _beneficiary, uint256 _amount) external onlyOwner {
        IERC20Upgradeable(nftl).safeTransfer(_beneficiary, _amount);

        emit NFTLWithdrawnByDAO(msg.sender, _beneficiary, _amount);
    }
}

contracts/HydraDistributor.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/utils/ERC721HolderUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";

/**
 * @title HydraDistributor
 * @notice Hydra is the 7th tribe of NiftyDegen.
 */
contract HydraDistributor is
    Initializable,
    OwnableUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable,
    ERC721HolderUpgradeable
{
    /// @dev NiftyDegen NFT address
    IERC721Upgradeable public niftyDegen;

    /// @dev Hydra Token Id list
    uint256[] public hydraTokenIds;

    /// @dev NiftyLeague Wallet Address
    address public niftyWallet;

    /// @dev Random Hash Value
    bytes32 internal _prevHash;

    event NiftyDegenSet(address indexed niftyDegen);
    event NiftyWalletSet(address indexed niftyWallet);
    event HydraClaimed(address indexed user, uint256[] tokenIdsBurned, uint256 hydraTokenId);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _niftyDegen, address _niftyWallet) public initializer {
        __Ownable_init();
        __Pausable_init();
        __ReentrancyGuard_init();

        require(_niftyDegen != address(0), "Zero address");
        require(_niftyWallet != address(0), "Zero address");

        niftyDegen = IERC721Upgradeable(_niftyDegen);
        niftyWallet = _niftyWallet;
    }

    /**
     * @notice Update the NiftyDegen NFT address
     * @param _niftyDegen NiftyDegen NFT address
     */
    function updateNiftyDegen(address _niftyDegen) external onlyOwner {
        require(_niftyDegen != address(0), "Zero address");

        niftyDegen = IERC721Upgradeable(_niftyDegen);

        emit NiftyDegenSet(_niftyDegen);
    }

    /**
     * @notice Update the NiftyLeague wallet address
     * @param _niftyWallet NiftyLeague wallet address
     */
    function updateNiftyWallet(address _niftyWallet) external onlyOwner {
        require(_niftyWallet != address(0), "Zero address");

        niftyWallet = _niftyWallet;

        emit NiftyWalletSet(_niftyWallet);
    }

    /**
     * @notice Deposit the Hydra
     * @param _hydraTokenIdList Token Ids of the Hydra to deposit
     */
    function depositHydra(uint256[] calldata _hydraTokenIdList) external onlyOwner {
        for (uint256 i = 0; i < _hydraTokenIdList.length; ) {
            uint256 tokenId = _hydraTokenIdList[i];

            hydraTokenIds.push(tokenId);

            unchecked {
                ++i;
            }
        }

        for (uint256 i = 0; i < _hydraTokenIdList.length; ) {
            uint256 tokenId = _hydraTokenIdList[i];

            niftyDegen.safeTransferFrom(msg.sender, address(this), tokenId, bytes(""));

            unchecked {
                ++i;
            }
        }
    }

    /**
     * @notice Claim the random Hydra
     * @dev The users must transfer 8 normal degens to claim 1 random Hydra
     * @dev NiftyWallet must transfer 12 normal degens to claim 1 random Hydra
     * @dev All the trasnferred the normal degens are burned
     * @param _degenTokenIdList Token Ids of the normal degens to burn
     */
    function claimRandomHydra(uint256[] calldata _degenTokenIdList) external nonReentrant whenNotPaused {
        uint256 degenCountToBurn = _degenTokenIdList.length;

        if (msg.sender == niftyWallet) {
            require(degenCountToBurn == 12, "Need 12 degens");
        } else {
            require(degenCountToBurn == 8, "Need 8 degens");
        }

        // get the random Hydra tokenId
        uint256 randomValue = 1;
        for (uint256 i = 0; i < degenCountToBurn; ) {
            unchecked {
                randomValue *= _degenTokenIdList[i]; // generate the random value, ignore overflow
                ++i;
            }
        }

        bytes32 randomHash = keccak256(
            abi.encodePacked(_prevHash, randomValue, msg.sender, block.timestamp, block.difficulty)
        );
        uint256 hydraCount = hydraTokenIds.length;
        uint256 hydraIndex = uint256(randomHash) % hydraCount;
        uint256 hydraTokenId = hydraTokenIds[hydraIndex];

        // remove the claimed rare degen Id from the list
        hydraTokenIds[hydraIndex] = hydraTokenIds[hydraCount - 1];
        hydraTokenIds.pop();

        // set the prevHash
        _prevHash = randomHash;

        // burn user's degens
        for (uint256 i = 0; i < degenCountToBurn; ) {
            niftyDegen.safeTransferFrom(msg.sender, address(1), _degenTokenIdList[i], bytes(""));

            unchecked {
                ++i;
            }
        }

        emit HydraClaimed(msg.sender, _degenTokenIdList, hydraTokenId);

        // transfer the random Hydra to the user
        niftyDegen.safeTransferFrom(address(this), msg.sender, hydraTokenId, bytes(""));
    }

    /**
     * @notice Returns the number of the Hydra in the contract
     * @return hydraCount Number of Hydra in the contract
     */
    function getHydraCount() external view returns (uint256 hydraCount) {
        hydraCount = hydraTokenIds.length;
    }

    /**
     * @notice Withdraw all Hydra
     * @param _to Address to receive the Hydra
     */
    function withdrawAllHydra(address _to) external onlyOwner {
        for (uint256 i = 0; i < hydraTokenIds.length; ) {
            uint256 tokenId = hydraTokenIds[i];

            niftyDegen.safeTransferFrom(address(this), _to, tokenId, bytes(""));

            unchecked {
                ++i;
            }
        }
    }

    /**
     * @notice Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    function getHydraTokenIds() external view returns (uint256[] memory) {
        return hydraTokenIds;
    }
}

contracts/imx/NiftyBurningComicsL2.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.11;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "../interfaces/INiftyLaunchComics.sol";

contract NiftyBurningComicsL2 is OwnableUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable {
    event ComicsBurned(address indexed by, uint256[] tokenIds, uint256[] values);
    event KeyMinted(address indexed by, uint256 tokenId, uint256 value, uint256 startIdForIMX);
    event ItemMinted(address indexed by, uint256[] tokenIds, uint256[] values, uint256[] startIdForIMX);

    /// @dev NiftyLaunchComics address
    address public comics;

    /// @dev Item index
    uint256 public itemIndex;

    /// @dev Token ID -> Item ID
    mapping(uint256 => uint256) public itemIdByTokenId;

    function initialize(address _comics) public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        comics = _comics;

        // set the current item index
        itemIndex = 1;
    }

    /**
     * @notice Burn comics and returns the items associated with its page
     * @dev User can burn all 6 comics at once to receive a key to the citadel
     * @param _values Number of comics to burn, nth value means the number of nth comics(tokenId = n) to burn
     */
    function burnComics(uint256[] calldata _values) external nonReentrant whenNotPaused {
        // check _values param
        require(_values.length == 6, "Invalid length");

        // tokenIds and values to be minted
        uint256[] memory tokenIds = new uint256[](6);
        uint256[] memory tokenNumbersForItems = new uint256[](6);
        uint256[] memory tokenItemIndexs = new uint256[](6);

        // get tokenIds and the number of keys to mint
        uint256 valueForKeys = type(uint256).max;
        for (uint256 i; i < _values.length; i++) {
            // burning comics for keys
            // get the min value in _values
            if (_values[i] < valueForKeys) valueForKeys = _values[i];

            // set tokenIds
            tokenIds[i] = i + 1;
        }

        // in case of the keys should be minted, set the number of items to be minted
        if (valueForKeys != 0) {
            for (uint256 i; i < _values.length; i++) {
                tokenNumbersForItems[i] = _values[i] - valueForKeys;
            }
        }

        // burn comics
        INiftyLaunchComics(comics).burnBatch(msg.sender, tokenIds, _values);
        emit ComicsBurned(msg.sender, tokenIds, _values);

        // mint the keys and items
        if (valueForKeys != 0) {
            // mint the key and items
            emit KeyMinted(msg.sender, 1, valueForKeys, itemIndex);

            // set the itemId by the tokenId
            for (uint256 i; i < valueForKeys; i++) {
                itemIdByTokenId[itemIndex + i] = 7; // 7: Key
            }

            // increase the itemIndex for next items
            itemIndex += valueForKeys;

            for (uint256 i; i < _values.length; i++) {
                tokenItemIndexs[i] = itemIndex;

                for (uint256 j; j < _values.length; j++) {
                    itemIdByTokenId[tokenItemIndexs[i] + j] = i + 1; // 1: Item1, 2: Item2, ..., 6 : Item6
                }

                // increase the itemIndex for next items
                itemIndex += tokenNumbersForItems[i];
            }

            emit ItemMinted(msg.sender, tokenIds, tokenNumbersForItems, tokenItemIndexs);
        } else {
            // mint items
            for (uint256 i; i < _values.length; i++) {
                tokenItemIndexs[i] = itemIndex;

                // set the itemId by the tokenId
                for (uint256 j; j < _values[i]; j++) {
                    itemIdByTokenId[tokenItemIndexs[i] + j] = i + 1; // 1: Item1, 2: Item2, ..., 6 : Item6
                }

                // increase the itemIndex for next items
                itemIndex += _values[i];
            }

            emit ItemMinted(msg.sender, tokenIds, _values, tokenItemIndexs);
        }
    }

    /**
     * @notice Pause comics burning
     * @dev Only owner
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause comics burning
     * @dev Only owner
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/imx/NiftyItemL2.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.11;

import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

import "@imtbl/imx-contracts/contracts/Mintable.sol";
import "@imtbl/imx-contracts/contracts/utils/Minting.sol";

/**
 * @dev {ERC721} token, including:
 */
contract NiftyItemL2 is ERC721EnumerableUpgradeable, OwnableUpgradeable, PausableUpgradeable, IMintable {
    using StringsUpgradeable for uint256;

    /// @dev Token URI
    string public uri;

    /// @dev IMX address
    address public imx;

    /// @dev TokenID -> Item ID
    /// @dev ItemID1 - item 1, ItemID 2 - item 2, ,,,, ItemID 6 - item6, ItemID 7 - key
    mapping(uint256 => uint256) public itemIdByTokenId;

    event AssetMinted(address to, uint256 id, bytes blueprint);

    modifier onlyOwnerOrIMX() {
        require(msg.sender == imx || msg.sender == owner(), "Function can only be called by owner or IMX");
        _;
    }

    function initialize(address _imx) external initializer {
        __ERC721_init("NiftyItemL2", "NiftyItemL2");
        __Ownable_init();
        __Pausable_init();

        imx = _imx;
    }

    function mintFor(
        address _to,
        uint256 _quantity,
        bytes calldata _mintingBlob
    ) external override onlyOwnerOrIMX whenNotPaused {
        require(_quantity == 1, "Amount must be 1");
        (uint256 id, bytes memory blueprint) = Minting.split(_mintingBlob);
        _mint(_to, id, blueprint);

        emit AssetMinted(_to, id, blueprint);
    }

    function _mint(address _to, uint256 _tokenId, bytes memory blueprint) internal {
        uint256 itemId = Bytes.toUint(blueprint);
        itemIdByTokenId[_tokenId] = itemId;

        _safeMint(_to, _tokenId);
    }

    function setURI(string calldata _uri) external onlyOwner {
        uri = _uri;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uri;
    }

    function burn(uint256 _tokenId) external whenNotPaused {
        _burn(_tokenId);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }
}

